mapper.delete:
delete from sys_module



      1 <> 1


      module_id = #{id}
      or module_parent_id = #{id}


      module_id in #{id}
      or module_parent_id in #{id}

mapper.select:
select count(*) from sys_module


    module_parent_id = #{moduleParentId,jdbcType=INTEGER}


    and valid_status = #{validStatus,jdbcType=CHAR}

select 

from sys_module


    module_parent_id = #{moduleParentId,jdbcType=INTEGER}


    and valid_status = #{validStatus,jdbcType=CHAR}


