mapper.delete:
delete from sys_operator



      1 <> 1


      id = #{id}


      id in #{id}

mapper.select:
select count(*) from sys_operator

  and operator_role_id <> '1'

    and operator_no = #{operatorNo,jdbcType=VARCHAR}


    and operator_role_id = #{operatorRoleId,jdbcType=VARCHAR}


    and valid_status = #{validStatus,jdbcType=CHAR}


    and operator_role_id like concat(#{loginRoleId,jdbcType=VARCHAR}, '%')

select 
sys_operator.id, operator_no, operator_name, operator_role_id, AES_DECRYPT(UNHEX(email),#{aesEmailKey,jdbcType=VARCHAR}) email,
AES_DECRYPT(UNHEX(tel),#{aesPhoneKey,jdbcType=VARCHAR}) tel, head_image, sys_operator.remark, sys_operator.valid_status, role_name
from sys_operator, sys_role

  operator_role_id = role_id and operator_role_id <> '1'

    and operator_no = #{operatorNo,jdbcType=VARCHAR}


    and operator_role_id = #{operatorRoleId,jdbcType=VARCHAR}


    and sys_operator.valid_status = #{validStatus,jdbcType=CHAR}


    and operator_role_id like concat(#{loginRoleId,jdbcType=VARCHAR}, '%')


order by operator_role_id asc

  limit #{start,jdbcType=INTEGER}, #{limit,jdbcType=INTEGER}

select count(*) from sys_operator

  and operator_role_id = '1'

    and master_no = #{loginNo,jdbcType=VARCHAR}


    and operator_no = #{subNo,jdbcType=VARCHAR}


    and master_no = #{masterNo,jdbcType=VARCHAR}


    and master_role_id = #{masterRoleId,jdbcType=VARCHAR}


    and valid_status = #{validStatus,jdbcType=CHAR}

select 
sys_operator.id, operator_no, operator_name, master_no, master_role_id, AES_DECRYPT(UNHEX(email),#{aesEmailKey,jdbcType=VARCHAR}) email,
AES_DECRYPT(UNHEX(tel),#{aesPhoneKey,jdbcType=VARCHAR}) tel, head_image, sys_operator.remark, sys_operator.valid_status, role_name
from sys_operator, sys_role

  master_role_id = role_id and operator_role_id = '1'

    and master_no = #{loginNo,jdbcType=VARCHAR}


    and operator_no = #{subNo,jdbcType=VARCHAR}


    and master_no = #{masterNo,jdbcType=VARCHAR}


    and master_role_id = #{masterRoleId,jdbcType=VARCHAR}


    and sys_operator.valid_status = #{validStatus,jdbcType=CHAR}



  limit #{start,jdbcType=INTEGER}, #{limit,jdbcType=INTEGER}
