mapper.delete:
delete from sys_param



      1 <> 1


      id = #{id}


      id in #{id}

mapper.select:
select count(*) from sys_param


    and param_code = #{paramCode,jdbcType=VARCHAR}


    and valid_status = #{validStatus,jdbcType=CHAR}


    and FIND_IN_SET(#{loginRoleId,jdbcType=VARCHAR},if(modified_role_id is not null and modified_role_id <> '', modified_role_id, #{loginRoleId,jdbcType=VARCHAR}))

select 

from sys_param


    and param_code = #{paramCode,jdbcType=VARCHAR}


    and valid_status = #{validStatus,jdbcType=CHAR}


    and FIND_IN_SET(#{loginRoleId,jdbcType=VARCHAR},if(modified_role_id is not null and modified_role_id <> '', modified_role_id, #{loginRoleId,jdbcType=VARCHAR}))


order by priority asc

  limit #{start,jdbcType=INTEGER}, #{limit,jdbcType=INTEGER}

mapper.update:
update sys_param t1, sys_param t2, (select 

    max(priority) priority
    min(priority) priority

  from sys_param



        priority < #{priority}
        priority > #{priority}



) t3 set

  t1.priority = t3.priority, t2.priority = t2.priority + 1
  t1.priority = t2.priority, t2.priority = t1.priority


  t1.priority = #{priority}

    and t2.priority < #{priority}
    and t2.priority = t3.priority
